      };

      const startDateStr = formatDate(startDate);
      const endDateStr = formatDate(endDate);

      console.log(
        `ğŸ“Š ì¼ë´‰ ë°ì´í„° ìš”ì²­: ${symbol}, ${startDateStr} ~ ${endDateStr}`
      );

