          rawData: sampledData, // ìƒ˜í”Œë§ëœ ë°ì´í„° ë³´ê´€
          yAxisSegments: 6, // yì¶• ì„¸ê·¸ë¨¼íŠ¸ëŠ” ê³ ì •ìœ¼ë¡œ 6ê°œ
        });
      } else {
        console.warn("ì¼ë´‰ ë°ì´í„°ë¥¼ ë¶ˆëŸ¬ì˜¬ ìˆ˜ ì—†ìŠµë‹ˆë‹¤:", data);
        setChartData(null);
      }
    } catch (error) {
      console.error("ì¼ë´‰ ë°ì´í„° ìš”ì²­ ì‹¤íŒ¨:", error);
      setChartData(null);
