          <Text style={styles.chartText}>ì°¨íŠ¸ ë°ì´í„°ë¥¼ ë¶ˆëŸ¬ì˜¬ ìˆ˜ ì—†ìŠµë‹ˆë‹¤</Text>
        </View>
      );
    }

    return (
      <View style={styles.chartContainer}>
        <LineChart
