import React, { useState } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  Alert,
  StyleSheet,
  ScrollView,
} from "react-native";
import { API_BASE_URL } from "../../utils/apiConfig";

const ResetPasswordScreen = ({ route, navigation }) => {
  // route.paramsÍ∞Ä ÏóÜÎäî Í≤ΩÏö∞ Ï≤òÎ¶¨
  const { email = "" } = route?.params || {};
  const [resetToken, setResetToken] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const handleResetPassword = async () => {
    // ÏûÖÎ†•Í∞í Í≤ÄÏ¶ù
    if (!resetToken) {
      Alert.alert("Ïò§Î•ò", "Ïû¨ÏÑ§Ï†ï ÌÜ†ÌÅ∞ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      return;
    }
    if (!newPassword) {
      Alert.alert("Ïò§Î•ò", "ÏÉà ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      return;
    }
    if (newPassword !== confirmPassword) {
      Alert.alert("Ïò§Î•ò", "ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§.");
      return;
    }
    if (newPassword.length < 8) {
      Alert.alert("Ïò§Î•ò", "ÎπÑÎ∞ÄÎ≤àÌò∏Îäî 8Ïûê Ïù¥ÏÉÅÏù¥Ïñ¥Ïïº Ìï©ÎãàÎã§.");
      return;
    }

    setLoading(true);
    try {
      const response = await fetch(
        `${API_BASE_URL}users/password_reset/confirm/`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            email,
            reset_token: resetToken,
            new_password: newPassword,
          }),
        }
      );

      // ÏùëÎãµ ÌÉÄÏûÖ ÌôïÏù∏
      const contentType = response.headers.get("content-type");
      if (!contentType || !contentType.includes("application/json")) {
        const textResponse = await response.text();
        console.error("ÏÑúÎ≤Ñ ÏùëÎãµÏù¥ JSONÏù¥ ÏïÑÎãò:", textResponse);
        Alert.alert("Ïò§Î•ò", "ÏÑúÎ≤Ñ ÏùëÎãµ ÌòïÏãù Ïò§Î•ò");
        setLoading(false);
        return;
      }

      const data = await response.json();

      if (response.ok) {
        Alert.alert(
          "ÏÑ±Í≥µ",
          data.message || "ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Î≥ÄÍ≤ΩÎêòÏóàÏäµÎãàÎã§.",
          [
            {
              text: "Î°úÍ∑∏Ïù∏ÌïòÍ∏∞",
              onPress: () => navigation.navigate("Login"),
            },
          ]
        );
      } else {
        Alert.alert("Ïò§Î•ò", data.message || "ÎπÑÎ∞ÄÎ≤àÌò∏ Ïû¨ÏÑ§Ï†ïÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
      }
    } catch (error) {
      console.error("üö® Network Error:", error);
      Alert.alert("Ïò§Î•ò", "ÎÑ§Ìä∏ÏõåÌÅ¨ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <ScrollView contentContainerStyle={styles.scrollContainer}>
      <View style={styles.container}>
        {/* üîô Îí§Î°ú Í∞ÄÍ∏∞ Î≤ÑÌäº */}
        <TouchableOpacity
          onPress={() => navigation.goBack()}
          style={styles.backButton}
        >
          <Text style={styles.backText}>{"<"}</Text>
        </TouchableOpacity>

        {/* üè∑ ÌÉÄÏù¥ÌãÄ */}
        <Text style={styles.title}>ÎπÑÎ∞ÄÎ≤àÌò∏ Ïû¨ÏÑ§Ï†ï</Text>

        <View style={styles.formContainer}>
          {/* Ïù¥Î©îÏùº ÌëúÏãú */}
          <Text style={styles.emailText}>{email}</Text>

          {/* ÌÜ†ÌÅ∞ ÏûÖÎ†• */}
          <Text style={styles.label}>Ïû¨ÏÑ§Ï†ï ÌÜ†ÌÅ∞</Text>
          <View style={styles.inputContainer}>
            <TextInput
              style={styles.input}
              placeholder="Ïù¥Î©îÏùºÎ°ú Î∞õÏùÄ Ïû¨ÏÑ§Ï†ï ÌÜ†ÌÅ∞ÏùÑ ÏûÖÎ†•Ìï¥ Ï£ºÏÑ∏Ïöî"
              placeholderTextColor="#ccc"
              value={resetToken}
              onChangeText={setResetToken}
            />
          </View>

          {/* ÏÉà ÎπÑÎ∞ÄÎ≤àÌò∏ ÏûÖÎ†• */}
          <Text style={styles.label}>ÏÉà ÎπÑÎ∞ÄÎ≤àÌò∏</Text>
          <View style={styles.inputContainer}>
            <TextInput
              style={styles.input}
              placeholder="8Ïûê Ïù¥ÏÉÅ ÏûÖÎ†•"
              placeholderTextColor="#ccc"
              secureTextEntry
              value={newPassword}
              onChangeText={setNewPassword}
            />
          </View>

          {/* ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏ ÏûÖÎ†• */}
          <Text style={styles.label}>ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏</Text>
          <View style={styles.inputContainer}>
            <TextInput
              style={styles.input}
              placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏Î•º Îã§Ïãú ÏûÖÎ†•Ìï¥ Ï£ºÏÑ∏Ïöî"
              placeholderTextColor="#ccc"
              secureTextEntry
              value={confirmPassword}
              onChangeText={setConfirmPassword}
            />
          </View>

          {/* Î≥ÄÍ≤Ω Î≤ÑÌäº */}
          <TouchableOpacity
            style={[styles.resetButton, loading && styles.disabledButton]}
            onPress={handleResetPassword}
            disabled={loading}
          >
            <Text style={styles.resetButtonText}>
              {loading ? "Ï≤òÎ¶¨ Ï§ë..." : "ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥ÄÍ≤Ω"}
            </Text>
          </TouchableOpacity>

          <Text style={styles.infoText}>Ïù¥Î©îÏùºÎ°ú Î∞õÏùÄ ÌÜ†ÌÅ∞ÏùÑ ÏûÖÎ†•Ìïú ÌõÑ,</Text>
          <Text style={styles.infoText}>ÏÉà ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏÑ§Ï†ïÌï¥ Ï£ºÏÑ∏Ïöî.</Text>
        </View>
      </View>
    </ScrollView>
  );
};

// ‚úÖ Ïä§ÌÉÄÏùº Ï†ïÏùò
const styles = StyleSheet.create({
  scrollContainer: {
    flexGrow: 1,
  },
  container: {
    flex: 1,
    backgroundColor: "#003340",
    alignItems: "center",
    paddingHorizontal: 30,
    paddingBottom: 40,
  },

  backButton: {
    position: "absolute",
    top: 50,
    left: 20,
    zIndex: 10,
  },
  backText: {
    fontSize: 36,
    color: "#F074BA",
  },

  title: {
    fontSize: 24,
    fontWeight: "bold",
    color: "#F074BA",
    position: "absolute",
    top: 150,
    left: 30,
  },

  formContainer: {
    width: "100%",
    marginTop: 180,
    alignItems: "center",
  },

  emailText: {
    fontSize: 18,
    color: "#fff",
    marginBottom: 20,
    alignSelf: "left",
  },

  label: {
    fontSize: 16,
    color: "#F074BA",
    alignSelf: "flex-start",
    marginTop: 15,
    marginBottom: 10,
  },

  inputContainer: {
    width: "100%",
    borderWidth: 1,
    borderColor: "#ddd",
    borderRadius: 8,
    backgroundColor: "#f9f9f9",
    marginBottom: 10,
    paddingHorizontal: 10,
  },

  input: {
    height: 50,
    fontSize: 16,
    color: "black",
  },

  resetButton: {
    width: "100%",
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#F074BA",
    borderRadius: 8,
    marginTop: 30,
    marginBottom: 50,
  },

  disabledButton: {
    backgroundColor: "#A0A0A0",
  },

  resetButtonText: {
    fontSize: 16,
    fontWeight: "bold",
    color: "white",
  },

  infoText: {
    fontSize: 14,
    color: "#F074BA",
    textAlign: "center",
    // marginTop: 20,
    opacity: 0.7,
    fontWeight: "bold",
  },
});

export default ResetPasswordScreen;
