import React, { useEffect, useState } from "react";
import {
  View,
  Text,
  StyleSheet,
  Image,
  ActivityIndicator,
  Alert,
  ScrollView,
  TouchableOpacity,
} from "react-native";
import Icon from "react-native-vector-icons/Feather";
import { API_BASE_URL } from "../../utils/apiConfig";

import { getNewAccessToken } from "../../utils/token";
import { fetchUserInfo } from "../../utils/user";
import { fetchUserMbtiType, getMbtiImage } from "../../utils/mbtiType";
import { increaseBalance } from "../../utils/point";


const MyPageScreen = ({ navigation }) => {
  console.log("üìå MyPageScreen Î†åÎçîÎßÅ");

  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [badgeList, setBadgeList] = useState([]); // Ï†ÑÏ≤¥ Î±ÉÏßÄ

  const [equippedBadges, setEquippedBadges] = useState(["üî•", "üåü", "üíØ"]);
  const [introText, setIntroText] = useState("Ìã∞ÎÅå Î™®ÏïÑ ÌÉúÏÇ∞Ïù¥Í∏¥Ìï¥!");
  const [isEditingIntro, setIsEditingIntro] = useState(false);

  //const profileImage = require("../../assets/profile.png");
  const [mbtiType, setMbtiType] = useState(null);

  const DEPOSIT_AMOUNT = 100000;


useEffect(() => {
  fetchUserMbtiType(navigation, setMbtiType);
}, []);


  const saveIntroText = async (text) => {
    try {
      // ÏÑúÎ≤ÑÎ°ú PATCH ÏöîÏ≤≠
      // await updateIntroAPI(text);
      console.log("‚úî ÌïúÏ§ÑÏÜåÍ∞ú Ï†ÄÏû•Îê®:", text);
    } catch (err) {
      Alert.alert("Ï†ÄÏû• Ïã§Ìå®", "ÌïúÏ§ÑÏÜåÍ∞ú Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
    }
  };

  const MenuButton = ({ label, onPress }) => (
    <TouchableOpacity style={styles.menuButton} onPress={onPress}>
      <View style={styles.menuRow}>
        <Text style={styles.menuText}>{label}</Text>
        <Icon name="chevron-right" size={20} color="#ffffff" />
      </View>
    </TouchableOpacity>
  );

  // const handleLogout = () => {
  //   Alert.alert("Î°úÍ∑∏ÏïÑÏõÉ", "Ï†ïÏÉÅÏ†ÅÏúºÎ°ú Î°úÍ∑∏ÏïÑÏõÉÎêòÏóàÏäµÎãàÎã§.");
  //   navigation.navigate("Login");
  // };

  const handleLogout = async () => {
  try {
    const accessToken = await getNewAccessToken(navigation);
    if (!accessToken) {
      Alert.alert(
        "Ïù∏Ï¶ù Ïò§Î•ò",
        "ÌÜ†ÌÅ∞Ïù¥ ÎßåÎ£åÎêòÏóàÏäµÎãàÎã§. Îã§Ïãú Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî."
      );
      navigation.navigate("Login");
      return;
    }

    const response = await fetch(`${API_BASE_URL}logout/`, {
      method: "POST",
      headers: {
        Authorization: `Bearer ${accessToken}`,
        "Content-Type": "application/json",
      },
    });

    if (response.ok) {
      Alert.alert("Î°úÍ∑∏ÏïÑÏõÉ", "Ï†ïÏÉÅÏ†ÅÏúºÎ°ú Î°úÍ∑∏ÏïÑÏõÉÎêòÏóàÏäµÎãàÎã§.");
      navigation.navigate("Login");
    } else {
      const text = await response.text();
      console.error("Î°úÍ∑∏ÏïÑÏõÉ Ïã§Ìå® ÏùëÎãµ:", text);
      Alert.alert("Ïò§Î•ò", "Î°úÍ∑∏ÏïÑÏõÉÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
    }
  } catch (err) {
    console.error("Î°úÍ∑∏ÏïÑÏõÉ Ï§ë Ïò§Î•ò:", err);
    Alert.alert("Ïò§Î•ò", "ÎÑ§Ìä∏ÏõåÌÅ¨ Ïò§Î•òÎ°ú Î°úÍ∑∏ÏïÑÏõÉÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
  }
};


  const handleDeleteAccount = () => {
    Alert.alert(
      "ÌöåÏõê ÌÉàÌá¥",
      "Ï†ïÎßê ÌÉàÌá¥ÌïòÏãúÍ≤†ÏäµÎãàÍπå?\nÏù¥ ÏûëÏóÖÏùÄ ÎêòÎèåÎ¶¥ Ïàò ÏóÜÏäµÎãàÎã§.",
      [
        { text: "Ï∑®ÏÜå", style: "cancel" },
        {
          text: "ÌÉàÌá¥ÌïòÍ∏∞",
          style: "destructive",
          onPress: async () => {
            try {
              const accessToken = await getNewAccessToken(navigation);
              if (!accessToken) {
                Alert.alert(
                  "Ïù∏Ï¶ù Ïò§Î•ò",
                  "ÌÜ†ÌÅ∞Ïù¥ ÎßåÎ£åÎêòÏóàÏäµÎãàÎã§. Îã§Ïãú Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî."
                );
                navigation.navigate("Login");
                return;
              }

              const response = await fetch(
                `${API_BASE_URL}users/delete/`,
                {
                  method: "DELETE",
                  headers: {
                    Authorization: `Bearer ${accessToken}`,
                    "Content-Type": "application/json",
                  },
                }
              );

              if (response.ok) {
                Alert.alert("ÌÉàÌá¥ ÏôÑÎ£å", "Í≥ÑÏ†ïÏù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.");
                navigation.navigate("Login");
              } else {
                const text = await response.text();
                console.error("ÌöåÏõê ÌÉàÌá¥ Ïã§Ìå® ÏùëÎãµ:", text);
                Alert.alert("Ïò§Î•ò", "ÌöåÏõê ÌÉàÌá¥Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
              }
            } catch (err) {
              console.error("ÌöåÏõê ÌÉàÌá¥ Ï§ë Ïò§Î•ò:", err);
              Alert.alert("Ïò§Î•ò", "ÎÑ§Ìä∏ÏõåÌÅ¨ Ïò§Î•òÎ°ú ÌÉàÌá¥Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
            }
          },
        },
      ]
    );
  };

  useEffect(() => {
    const loadUserData = async () => {
      try {
