import React, { useEffect, useState } from "react";
import {
  View,
  Text,
  TouchableOpacity,
  TextInput,
  KeyboardAvoidingView,
  Platform,
  StyleSheet,
  Alert,
  ActivityIndicator,
  SafeAreaView,
} from "react-native";
import { getNewAccessToken } from "../../utils/token";
import { fetchUserInfo } from "../../utils/user";
import { API_BASE_URL } from "../../utils/apiConfig";

const TradingSellScreen = ({ route, navigation }) => {
  const stock = route.params?.stock;
  const [quantity, setQuantity] = useState("1");
  const [currentPrice, setCurrentPrice] = useState(0);
  const [userId, setUserId] = useState(null);
  const [loading, setLoading] = useState(false);
  const [priceLoading, setPriceLoading] = useState(true);

  useEffect(() => {
    const init = async () => {
      // ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
      await fetchUserInfo(navigation, (info) => {
        if (info?.id) setUserId(info.id);
      });

      // ÌòÑÏû¨Í∞Ä Í∞ÄÏ†∏Ïò§Í∏∞
      await fetchCurrentPrice();
    };
    init();
  }, []);

  const fetchCurrentPrice = async () => {
    if (!stock?.symbol) {
      console.error("‚ùå Ï¢ÖÎ™© ÏΩîÎìúÍ∞Ä ÏóÜÏäµÎãàÎã§.");
      setPriceLoading(false);
      return;
    }

    try {
      setPriceLoading(true);

      const response = await fetch(
        `${API_BASE_URL}trading/stock_price/?stock_code=${stock.symbol}`
      );

      if (!response.ok) {
        throw new Error(`Price API error: ${response.status}`);
      }

      const result = await response.json();

      if (result.status === "success" && result.current_price) {
        setCurrentPrice(result.current_price);
        console.log("‚úÖ ÌòÑÏû¨Í∞Ä ÏóÖÎç∞Ïù¥Ìä∏:", result.current_price);
      } else {
        console.warn("‚ö†Ô∏è ÌòÑÏû¨Í∞Ä API ÏùëÎãµ Ïã§Ìå®:", result);
        // Í∏∞Ï°¥ Ï£ºÏãù Í∞ÄÍ≤©ÏùÑ ÏÇ¨Ïö©
        setCurrentPrice(
          typeof stock.price === "string"
            ? parseInt(stock.price.replace(/,/g, ""))
            : stock.price
        );
      }
    } catch (error) {
      console.error("‚ùå ÌòÑÏû¨Í∞Ä Ï°∞Ìöå Ïã§Ìå®:", error);
      // Í∏∞Ï°¥ Ï£ºÏãù Í∞ÄÍ≤©ÏùÑ ÏÇ¨Ïö©
      setCurrentPrice(
        typeof stock.price === "string"
          ? parseInt(stock.price.replace(/,/g, ""))
          : stock.price
      );
    } finally {
      setPriceLoading(false);
    }
  };

  const calculateTotal = () => {
    const qty = parseInt(quantity) || 0;
    return currentPrice * qty;
  };

  const handleSell = async () => {
    console.log("üí∏ Îß§ÎèÑ Ï£ºÎ¨∏ ÏãúÏûë");

    if (!stock || !stock.name) {
      Alert.alert("Ïò§Î•ò", "Ï£ºÏãù Ï†ïÎ≥¥Í∞Ä Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏäµÎãàÎã§.");
      return;
    }

    const qty = parseInt(quantity);
    if (!qty || qty <= 0) {
      Alert.alert("Ïò§Î•ò", "Ïò¨Î∞îÎ•∏ ÏàòÎüâÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      return;
    }

    const ownedQty = parseInt(stock.quantity) || 0;
    if (qty > ownedQty) {
      Alert.alert(
        "Îß§ÎèÑ Ïã§Ìå®",
        `Î≥¥Ïú† ÏàòÎüâ(${ownedQty}Ï£º)Î≥¥Îã§ ÎßéÏù¥ Îß§ÎèÑÌï† Ïàò ÏóÜÏäµÎãàÎã§.`
      );
      return;
    }

    if (currentPrice <= 0) {
      Alert.alert("Ïò§Î•ò", "Ï£ºÏãù Í∞ÄÍ≤© Ï†ïÎ≥¥Í∞Ä Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏäµÎãàÎã§.");
      return;
    }

    setLoading(true);

    try {
      const accessToken = await getNewAccessToken(navigation);
      if (!accessToken || !userId) {
        Alert.alert("Ïò§Î•ò", "ÏÇ¨Ïö©Ïûê Ïù∏Ï¶ùÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
        return;
      }

      // Ï¢ÖÎ™© ÏãùÎ≥ÑÏûê Í≤∞Ï†ï (Ï¢ÖÎ™©ÏΩîÎìú Ïö∞ÏÑ† ÏÇ¨Ïö©)
      const stockIdentifier = stock.symbol || stock.name;

      const orderData = {
        user_id: userId,
        stock_symbol: stockIdentifier,
        order_type: "sell",
        quantity: qty,
        price: currentPrice,
      };

      console.log("üì° Îß§ÎèÑ Ï£ºÎ¨∏ Îç∞Ïù¥ÌÑ∞:", orderData);

      const response = await fetch(`${API_BASE_URL}trading/trade/`, {
        method: "POST",
        headers: {
          Authorization: `Bearer ${accessToken}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(orderData),
      });

      const result = await response.json();
      console.log("üì¨ Îß§ÎèÑ Ï£ºÎ¨∏ ÏùëÎãµ:", result);

      if (response.ok && result?.status === "success") {
        Alert.alert(
          "Îß§ÎèÑ ÏôÑÎ£å",
          result.message || `${stock.name} ${qty}Ï£º Îß§ÎèÑÍ∞Ä ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§.`,
          [{ text: "ÌôïÏù∏", onPress: () => navigation.goBack() }]
        );
      } else {
        Alert.alert(
          "Îß§ÎèÑ Ïã§Ìå®",
          result?.message || `Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. (${response.status})`
        );
      }
    } catch (error) {
      console.error("‚ùå Îß§ÎèÑ Ï£ºÎ¨∏ Ïã§Ìå®:", error);
      Alert.alert("ÏöîÏ≤≠ Ïã§Ìå®", "Îß§ÎèÑ Ï£ºÎ¨∏ Ï§ë Î¨∏Ï†úÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    } finally {
